#!/bin/bash
# Uninstall script for MiniSim
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MiniSim..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MiniSim if running..."
pkill -f "MiniSim" 2>/dev/null || true

# Kill application with bundle ID com.oskarkwasniewski.MiniSim if running
echo "Stopping application with bundle ID com.oskarkwasniewski.MiniSim if running..."
killall -9 "com.oskarkwasniewski.MiniSim" 2>/dev/null || true

# Remove /Applications/MiniSim.app
echo "Removing /Applications/MiniSim.app..."
if [ -d "/Applications/MiniSim.app" ]; then
    rm -rf "/Applications/MiniSim.app" 2>/dev/null || true
elif [ -f "/Applications/MiniSim.app" ]; then
    rm -f "/Applications/MiniSim.app" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.oskarkwasniewski.MiniSim
