if [ -d "$HOME/Library/HTTPStorages/com.oskarkwasniewski.MiniSim" ]; then
    rm -rf "$HOME/Library/HTTPStorages/com.oskarkwasniewski.MiniSim" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/com.oskarkwasniewski.MiniSim" ]; then
    rm -f "$HOME/Library/HTTPStorages/com.oskarkwasniewski.MiniSim" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.oskarkwasniewski.MiniSim.plist
echo "Removing $HOME/Library/Preferences/com.oskarkwasniewski.MiniSim.plist..."
