#!/bin/bash
# Uninstall script for Mission Control Plus
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mission Control Plus..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mission Control Plus if running..."
pkill -f "Mission Control Plus" 2>/dev/null || true

# Remove /Applications/Mission Control Plus.app
echo "Removing /Applications/Mission Control Plus.app..."
if [ -d "/Applications/Mission Control Plus.app" ]; then
    rm -rf "/Applications/Mission Control Plus.app" 2>/dev/null || true
elif [ -f "/Applications/Mission Control Plus.app" ]; then
    rm -f "/Applications/Mission Control Plus.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/io.fadel.missioncontrolplus.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/io.fadel.missioncontrolplus.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/io.fadel.missioncontrolplus.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/io.fadel.missioncontrolplus.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/io.fadel.missioncontrolplus.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/io.fadel.missioncontrolplus.sfl*" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
