#!/bin/bash
# Uninstall script for Missive
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Missive..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Missive if running..."
pkill -f "Missive" 2>/dev/null || true

# Remove /Applications/Missive.app
echo "Removing /Applications/Missive.app..."
if [ -d "/Applications/Missive.app" ]; then
    rm -rf "/Applications/Missive.app" 2>/dev/null || true
elif [ -f "/Applications/Missive.app" ]; then
    rm -f "/Applications/Missive.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Missive
echo "Removing $HOME/Library/Application Support/Missive..."
if [ -d "$HOME/Library/Application Support/Missive" ]; then
    rm -rf "$HOME/Library/Application Support/Missive" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Missive" ]; then
    rm -f "$HOME/Library/Application Support/Missive" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Missive
echo "Removing $HOME/Library/Logs/Missive..."
if [ -d "$HOME/Library/Logs/Missive" ]; then
    rm -rf "$HOME/Library/Logs/Missive" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Missive" ]; then
    rm -f "$HOME/Library/Logs/Missive" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.missiveapp.osx.plist
echo "Removing $HOME/Library/Preferences/com.missiveapp.osx.plist..."
if [ -d "$HOME/Library/Preferences/com.missiveapp.osx.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.missiveapp.osx.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.missiveapp.osx.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.missiveapp.osx.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.missiveapp.osx.savedState
echo "Removing $HOME/Library/Saved Application State/com.missiveapp.osx.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.missiveapp.osx.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.missiveapp.osx.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.missiveapp.osx.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.missiveapp.osx.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
