#!/bin/bash
# Uninstall script for Moom
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Moom..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Moom if running..."
pkill -f "Moom" 2>/dev/null || true

# Remove /Applications/Moom.app
echo "Removing /Applications/Moom.app..."
if [ -d "/Applications/Moom.app" ]; then
    rm -rf "/Applications/Moom.app" 2>/dev/null || true
elif [ -f "/Applications/Moom.app" ]; then
    rm -f "/Applications/Moom.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Many Tricks
echo "Removing $HOME/Library/Application Support/Many Tricks..."
if [ -d "$HOME/Library/Application Support/Many Tricks" ]; then
    rm -rf "$HOME/Library/Application Support/Many Tricks" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Many Tricks" ]; then
    rm -f "$HOME/Library/Application Support/Many Tricks" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.manytricks.Moom.plist
echo "Removing $HOME/Library/Preferences/com.manytricks.Moom.plist..."
if [ -d "$HOME/Library/Preferences/com.manytricks.Moom.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.manytricks.Moom.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.manytricks.Moom.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.manytricks.Moom.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
