fi

# Remove $HOME/Library/Saved Application State/com.electron.mqttx.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.mqttx.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.mqttx.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.electron.mqttx.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.electron.mqttx.savedState" ]; then
