#!/bin/bash
# Uninstall script for muCommander
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling muCommander..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping muCommander if running..."
pkill -f "muCommander" 2>/dev/null || true

# Remove /Applications/muCommander.app
echo "Removing /Applications/muCommander.app..."
if [ -d "/Applications/muCommander.app" ]; then
    rm -rf "/Applications/muCommander.app" 2>/dev/null || true
elif [ -f "/Applications/muCommander.app" ]; then
    rm -f "/Applications/muCommander.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/muCommander
echo "Removing $HOME/Library/Preferences/muCommander..."
if [ -d "$HOME/Library/Preferences/muCommander" ]; then
    rm -rf "$HOME/Library/Preferences/muCommander" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/muCommander" ]; then
    rm -f "$HOME/Library/Preferences/muCommander" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
