#!/bin/bash
# Uninstall script for Nagstamon
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Nagstamon..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Nagstamon if running..."
pkill -f "Nagstamon" 2>/dev/null || true

# Remove /Applications/Nagstamon.app
echo "Removing /Applications/Nagstamon.app..."
if [ -d "/Applications/Nagstamon.app" ]; then
    rm -rf "/Applications/Nagstamon.app" 2>/dev/null || true
elif [ -f "/Applications/Nagstamon.app" ]; then
    rm -f "/Applications/Nagstamon.app" 2>/dev/null || true
fi

# Remove $HOME/.nagstamon
echo "Removing $HOME/.nagstamon..."
if [ -d "$HOME/.nagstamon" ]; then
    rm -rf "$HOME/.nagstamon" 2>/dev/null || true
elif [ -f "$HOME/.nagstamon" ]; then
    rm -f "$HOME/.nagstamon" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
