#!/bin/bash
# Uninstall script for NameChanger
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling NameChanger..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping NameChanger if running..."
pkill -f "NameChanger" 2>/dev/null || true

# Remove /Applications/NameChanger.app
echo "Removing /Applications/NameChanger.app..."
if [ -d "/Applications/NameChanger.app" ]; then
    rm -rf "/Applications/NameChanger.app" 2>/dev/null || true
elif [ -f "/Applications/NameChanger.app" ]; then
    rm -f "/Applications/NameChanger.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.mrrsoftware.namechanger.sfl*
