    rm -rf "$HOME/Library/Application Support/NameChanger" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/NameChanger" ]; then
    rm -f "$HOME/Library/Application Support/NameChanger" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.mrrsoftware.NameChanger.plist
echo "Removing $HOME/Library/Preferences/com.mrrsoftware.NameChanger.plist..."
if [ -d "$HOME/Library/Preferences/com.mrrsoftware.NameChanger.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.mrrsoftware.NameChanger.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.mrrsoftware.NameChanger.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.mrrsoftware.NameChanger.plist" 2>/dev/null || true
fi

