#!/bin/bash
# Uninstall script for NAPS2
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling NAPS2..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping NAPS2 if running..."
pkill -f "NAPS2" 2>/dev/null || true

# Remove $HOME/.config/NAPS2
echo "Removing $HOME/.config/NAPS2..."
if [ -d "$HOME/.config/NAPS2" ]; then
    rm -rf "$HOME/.config/NAPS2" 2>/dev/null || true
elif [ -f "$HOME/.config/NAPS2" ]; then
    rm -f "$HOME/.config/NAPS2" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.naps2.desktop.plist
echo "Removing $HOME/Library/Preferences/com.naps2.desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.naps2.desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.naps2.desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.naps2.desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.naps2.desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.naps2.desktop.savedState
echo "Removing $HOME/Library/Saved Application State/com.naps2.desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.naps2.desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.naps2.desktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.naps2.desktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.naps2.desktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
