elif [ -f "$HOME/Library/Saved Application State/com.lutzroeder.netron.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.lutzroeder.netron.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
