#!/bin/bash
# Uninstall script for NetSpot
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling NetSpot..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping NetSpot if running..."
pkill -f "NetSpot" 2>/dev/null || true

# Remove /Applications/NetSpot.app
echo "Removing /Applications/NetSpot.app..."
if [ -d "/Applications/NetSpot.app" ]; then
    rm -rf "/Applications/NetSpot.app" 2>/dev/null || true
elif [ -f "/Applications/NetSpot.app" ]; then
    rm -f "/Applications/NetSpot.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/NetSpot
echo "Removing $HOME/Library/Application Support/NetSpot..."
if [ -d "$HOME/Library/Application Support/NetSpot" ]; then
    rm -rf "$HOME/Library/Application Support/NetSpot" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/NetSpot" ]; then
    rm -f "$HOME/Library/Application Support/NetSpot" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.etwok.netspotwifi
echo "Removing $HOME/Library/Caches/com.etwok.netspotwifi..."
if [ -d "$HOME/Library/Caches/com.etwok.netspotwifi" ]; then
    rm -rf "$HOME/Library/Caches/com.etwok.netspotwifi" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.etwok.netspotwifi" ]; then
    rm -f "$HOME/Library/Caches/com.etwok.netspotwifi" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.etwok.netspotwifi.plist
echo "Removing $HOME/Library/Preferences/com.etwok.netspotwifi.plist..."
if [ -d "$HOME/Library/Preferences/com.etwok.netspotwifi.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.etwok.netspotwifi.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.etwok.netspotwifi.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.etwok.netspotwifi.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
