#!/bin/bash
# Uninstall script for Nightfall
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Nightfall..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Nightfall if running..."
pkill -f "Nightfall" 2>/dev/null || true

# Remove /Applications/Nightfall.app
echo "Removing /Applications/Nightfall.app..."
if [ -d "/Applications/Nightfall.app" ]; then
    rm -rf "/Applications/Nightfall.app" 2>/dev/null || true
elif [ -f "/Applications/Nightfall.app" ]; then
    rm -f "/Applications/Nightfall.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.ryanthomson.Nightfall
echo "Removing $HOME/Library/Application Scripts/com.ryanthomson.Nightfall..."
if [ -d "$HOME/Library/Application Scripts/com.ryanthomson.Nightfall" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.ryanthomson.Nightfall" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.ryanthomson.Nightfall" ]; then
    rm -f "$HOME/Library/Application Scripts/com.ryanthomson.Nightfall" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.ryanthomson.Nightfall
echo "Removing $HOME/Library/Containers/com.ryanthomson.Nightfall..."
if [ -d "$HOME/Library/Containers/com.ryanthomson.Nightfall" ]; then
    rm -rf "$HOME/Library/Containers/com.ryanthomson.Nightfall" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.ryanthomson.Nightfall" ]; then
    rm -f "$HOME/Library/Containers/com.ryanthomson.Nightfall" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
