#!/bin/bash
# Uninstall script for Nocturnal
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Nocturnal..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Nocturnal if running..."
pkill -f "Nocturnal" 2>/dev/null || true

# Remove /Applications/Nocturnal.app
echo "Removing /Applications/Nocturnal.app..."
if [ -d "/Applications/Nocturnal.app" ]; then
    rm -rf "/Applications/Nocturnal.app" 2>/dev/null || true
elif [ -f "/Applications/Nocturnal.app" ]; then
    rm -f "/Applications/Nocturnal.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
