echo "Removing /Applications/Notesnook.app..."
if [ -d "/Applications/Notesnook.app" ]; then
    rm -rf "/Applications/Notesnook.app" 2>/dev/null || true
elif [ -f "/Applications/Notesnook.app" ]; then
    rm -f "/Applications/Notesnook.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Notesnook
echo "Removing $HOME/Library/Application Support/Notesnook..."
