fi

# Remove $HOME/Library/Logs/Notesnook
echo "Removing $HOME/Library/Logs/Notesnook..."
if [ -d "$HOME/Library/Logs/Notesnook" ]; then
    rm -rf "$HOME/Library/Logs/Notesnook" 2>/dev/null || true
