#!/bin/bash
# Uninstall script for OpenAudible
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OpenAudible..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OpenAudible if running..."
pkill -f "OpenAudible" 2>/dev/null || true

# Remove /Applications/OpenAudible.app
echo "Removing /Applications/OpenAudible.app..."
if [ -d "/Applications/OpenAudible.app" ]; then
    rm -rf "/Applications/OpenAudible.app" 2>/dev/null || true
elif [ -f "/Applications/OpenAudible.app" ]; then
    rm -f "/Applications/OpenAudible.app" 2>/dev/null || true
fi

# Remove /Library/OpenAudible
echo "Removing /Library/OpenAudible..."
if [ -d "/Library/OpenAudible" ]; then
    rm -rf "/Library/OpenAudible" 2>/dev/null || true
elif [ -f "/Library/OpenAudible" ]; then
    rm -f "/Library/OpenAudible" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
