#!/bin/bash
# Uninstall script for OpenBoard
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OpenBoard..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OpenBoard if running..."
pkill -f "OpenBoard" 2>/dev/null || true

# Remove /Applications/OpenBoard.app
echo "Removing /Applications/OpenBoard.app..."
if [ -d "/Applications/OpenBoard.app" ]; then
    rm -rf "/Applications/OpenBoard.app" 2>/dev/null || true
elif [ -f "/Applications/OpenBoard.app" ]; then
    rm -f "/Applications/OpenBoard.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/OpenBoard
echo "Removing $HOME/Library/Application Support/OpenBoard..."
if [ -d "$HOME/Library/Application Support/OpenBoard" ]; then
    rm -rf "$HOME/Library/Application Support/OpenBoard" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/OpenBoard" ]; then
    rm -f "$HOME/Library/Application Support/OpenBoard" 2>/dev/null || true
fi

# Remove $HOME/Movies/OpenBoard
echo "Removing $HOME/Movies/OpenBoard..."
if [ -d "$HOME/Movies/OpenBoard" ]; then
    rm -rf "$HOME/Movies/OpenBoard" 2>/dev/null || true
elif [ -f "$HOME/Movies/OpenBoard" ]; then
    rm -f "$HOME/Movies/OpenBoard" 2>/dev/null || true
fi

# Remove $HOME/Music/OpenBoard
echo "Removing $HOME/Music/OpenBoard..."
if [ -d "$HOME/Music/OpenBoard" ]; then
    rm -rf "$HOME/Music/OpenBoard" 2>/dev/null || true
elif [ -f "$HOME/Music/OpenBoard" ]; then
    rm -f "$HOME/Music/OpenBoard" 2>/dev/null || true
fi

# Remove $HOME/Pictures/OpenBoard
echo "Removing $HOME/Pictures/OpenBoard..."
if [ -d "$HOME/Pictures/OpenBoard" ]; then
    rm -rf "$HOME/Pictures/OpenBoard" 2>/dev/null || true
elif [ -f "$HOME/Pictures/OpenBoard" ]; then
    rm -f "$HOME/Pictures/OpenBoard" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
