#!/bin/bash
# Uninstall script for OpenLens
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OpenLens..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OpenLens if running..."
pkill -f "OpenLens" 2>/dev/null || true

# Remove /Applications/OpenLens.app
echo "Removing /Applications/OpenLens.app..."
if [ -d "/Applications/OpenLens.app" ]; then
    rm -rf "/Applications/OpenLens.app" 2>/dev/null || true
elif [ -f "/Applications/OpenLens.app" ]; then
    rm -f "/Applications/OpenLens.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/OpenLens
echo "Removing $HOME/Library/Application Support/OpenLens..."
if [ -d "$HOME/Library/Application Support/OpenLens" ]; then
    rm -rf "$HOME/Library/Application Support/OpenLens" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/OpenLens" ]; then
    rm -f "$HOME/Library/Application Support/OpenLens" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/OpenLens
echo "Removing $HOME/Library/Logs/OpenLens..."
if [ -d "$HOME/Library/Logs/OpenLens" ]; then
    rm -rf "$HOME/Library/Logs/OpenLens" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/OpenLens" ]; then
    rm -f "$HOME/Library/Logs/OpenLens" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
