#!/bin/bash
# Uninstall script for OpenRefine
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OpenRefine..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OpenRefine if running..."
pkill -f "OpenRefine" 2>/dev/null || true

# Remove /Applications/OpenRefine.app
echo "Removing /Applications/OpenRefine.app..."
if [ -d "/Applications/OpenRefine.app" ]; then
    rm -rf "/Applications/OpenRefine.app" 2>/dev/null || true
elif [ -f "/Applications/OpenRefine.app" ]; then
    rm -f "/Applications/OpenRefine.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/OpenRefine
echo "Removing $HOME/Library/Application Support/OpenRefine..."
if [ -d "$HOME/Library/Application Support/OpenRefine" ]; then
    rm -rf "$HOME/Library/Application Support/OpenRefine" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/OpenRefine" ]; then
    rm -f "$HOME/Library/Application Support/OpenRefine" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.google.refine.Refine.savedState
echo "Removing $HOME/Library/Saved Application State/com.google.refine.Refine.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.google.refine.Refine.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.google.refine.Refine.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.google.refine.Refine.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.google.refine.Refine.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
