#!/bin/bash
# Uninstall script for OpenShot Video Editor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OpenShot Video Editor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OpenShot Video Editor if running..."
pkill -f "OpenShot Video Editor" 2>/dev/null || true

# Remove /Applications/OpenShot Video Editor.app
echo "Removing /Applications/OpenShot Video Editor.app..."
if [ -d "/Applications/OpenShot Video Editor.app" ]; then
    rm -rf "/Applications/OpenShot Video Editor.app" 2>/dev/null || true
elif [ -f "/Applications/OpenShot Video Editor.app" ]; then
    rm -f "/Applications/OpenShot Video Editor.app" 2>/dev/null || true
fi

# Remove $HOME/.openshot_qt
echo "Removing $HOME/.openshot_qt..."
if [ -d "$HOME/.openshot_qt" ]; then
    rm -rf "$HOME/.openshot_qt" 2>/dev/null || true
elif [ -f "$HOME/.openshot_qt" ]; then
    rm -f "$HOME/.openshot_qt" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/openshot
echo "Removing $HOME/Library/Application Support/openshot..."
if [ -d "$HOME/Library/Application Support/openshot" ]; then
    rm -rf "$HOME/Library/Application Support/openshot" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/openshot" ]; then
    rm -f "$HOME/Library/Application Support/openshot" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/openshot-qt.plist
echo "Removing $HOME/Library/Preferences/openshot-qt.plist..."
if [ -d "$HOME/Library/Preferences/openshot-qt.plist" ]; then
    rm -rf "$HOME/Library/Preferences/openshot-qt.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/openshot-qt.plist" ]; then
    rm -f "$HOME/Library/Preferences/openshot-qt.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
