#!/bin/bash
# Uninstall script for OpenToonz
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OpenToonz..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OpenToonz if running..."
pkill -f "OpenToonz" 2>/dev/null || true

# Remove $HOME/Library/Caches/OpenToonz
echo "Removing $HOME/Library/Caches/OpenToonz..."
if [ -d "$HOME/Library/Caches/OpenToonz" ]; then
    rm -rf "$HOME/Library/Caches/OpenToonz" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/OpenToonz" ]; then
    rm -f "$HOME/Library/Caches/OpenToonz" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/io.github.opentoonz.OpenToonz.savedState
echo "Removing $HOME/Library/Saved Application State/io.github.opentoonz.OpenToonz.savedState..."
if [ -d "$HOME/Library/Saved Application State/io.github.opentoonz.OpenToonz.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/io.github.opentoonz.OpenToonz.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/io.github.opentoonz.OpenToonz.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/io.github.opentoonz.OpenToonz.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
