#!/bin/bash
# Uninstall script for Optimus Player
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Optimus Player..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Optimus Player if running..."
pkill -f "Optimus Player" 2>/dev/null || true

# Remove /Applications/Optimus Player.app
echo "Removing /Applications/Optimus Player.app..."
if [ -d "/Applications/Optimus Player.app" ]; then
    rm -rf "/Applications/Optimus Player.app" 2>/dev/null || true
elif [ -f "/Applications/Optimus Player.app" ]; then
    rm -f "/Applications/Optimus Player.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/mo.darren.optimus.player.mac.plist
echo "Removing $HOME/Library/Preferences/mo.darren.optimus.player.mac.plist..."
if [ -d "$HOME/Library/Preferences/mo.darren.optimus.player.mac.plist" ]; then
    rm -rf "$HOME/Library/Preferences/mo.darren.optimus.player.mac.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/mo.darren.optimus.player.mac.plist" ]; then
    rm -f "$HOME/Library/Preferences/mo.darren.optimus.player.mac.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/mo.darren.optimus.player.mac.savedState
echo "Removing $HOME/Library/Saved Application State/mo.darren.optimus.player.mac.savedState..."
if [ -d "$HOME/Library/Saved Application State/mo.darren.optimus.player.mac.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/mo.darren.optimus.player.mac.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/mo.darren.optimus.player.mac.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/mo.darren.optimus.player.mac.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
