#!/bin/bash
# Uninstall script for osquery
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling osquery..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping osquery if running..."
pkill -f "osquery" 2>/dev/null || true

# Unload service com.facebook.osqueryd
echo "Unloading service com.facebook.osqueryd..."
launchctl unload -w /Library/LaunchAgents/com.facebook.osqueryd.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.facebook.osqueryd.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.facebook.osqueryd.plist 2>/dev/null || true

echo "Uninstallation complete!"
exit 0
