#!/bin/bash
# Uninstall script for Pacifist
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Pacifist..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Pacifist if running..."
pkill -f "Pacifist" 2>/dev/null || true

# Remove /Applications/Pacifist.app
echo "Removing /Applications/Pacifist.app..."
if [ -d "/Applications/Pacifist.app" ]; then
    rm -rf "/Applications/Pacifist.app" 2>/dev/null || true
elif [ -f "/Applications/Pacifist.app" ]; then
    rm -f "/Applications/Pacifist.app" 2>/dev/null || true
fi

# Remove binary /Applications/Pacifist.app/Pacifist.app/Contents/Resources/usr/bin/pacifist
echo "Removing binary /Applications/Pacifist.app/Pacifist.app/Contents/Resources/usr/bin/pacifist..."
if [ -f "/Applications/Pacifist.app/Pacifist.app/Contents/Resources/usr/bin/pacifist" ]; then
    rm -f "/Applications/Pacifist.app/Pacifist.app/Contents/Resources/usr/bin/pacifist" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.charlessoft.pacifist.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.charlessoft.pacifist.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.charlessoft.pacifist.sfl*" ]; then
