    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.charlessoft.pacifist.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.charlessoft.pacifist.plist
echo "Removing $HOME/Library/Preferences/com.charlessoft.pacifist.plist..."
if [ -d "$HOME/Library/Preferences/com.charlessoft.pacifist.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.charlessoft.pacifist.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.charlessoft.pacifist.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.charlessoft.pacifist.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.charlessoft.pacifist.savedState
