#!/bin/bash
# Uninstall script for Pale Moon
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Pale Moon..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Pale Moon if running..."
pkill -f "Pale Moon" 2>/dev/null || true

# Remove /Applications/Pale Moon.app
echo "Removing /Applications/Pale Moon.app..."
if [ -d "/Applications/Pale Moon.app" ]; then
    rm -rf "/Applications/Pale Moon.app" 2>/dev/null || true
elif [ -f "/Applications/Pale Moon.app" ]; then
    rm -f "/Applications/Pale Moon.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Pale Moon
echo "Removing $HOME/Library/Application Support/Pale Moon..."
if [ -d "$HOME/Library/Application Support/Pale Moon" ]; then
    rm -rf "$HOME/Library/Application Support/Pale Moon" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Pale Moon" ]; then
    rm -f "$HOME/Library/Application Support/Pale Moon" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Pale Moon
echo "Removing $HOME/Library/Caches/Pale Moon..."
if [ -d "$HOME/Library/Caches/Pale Moon" ]; then
    rm -rf "$HOME/Library/Caches/Pale Moon" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Pale Moon" ]; then
    rm -f "$HOME/Library/Caches/Pale Moon" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.mozilla.pale moon.plist
echo "Removing $HOME/Library/Preferences/org.mozilla.pale moon.plist..."
