# Remove $HOME/Library/Saved Application State/org.mozilla.white star.savedState
echo "Removing $HOME/Library/Saved Application State/org.mozilla.white star.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.mozilla.white star.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.mozilla.white star.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.mozilla.white star.savedState" ]; then
