# Remove /Applications/Path Finder.app
echo "Removing /Applications/Path Finder.app..."
if [ -d "/Applications/Path Finder.app" ]; then
    rm -rf "/Applications/Path Finder.app" 2>/dev/null || true
elif [ -f "/Applications/Path Finder.app" ]; then
    rm -f "/Applications/Path Finder.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Path Finder
echo "Removing $HOME/Library/Application Support/Path Finder..."
