#!/bin/bash
# Uninstall script for Pencil
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Pencil..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Pencil if running..."
pkill -f "Pencil" 2>/dev/null || true

# Remove /Applications/Pencil.app
echo "Removing /Applications/Pencil.app..."
if [ -d "/Applications/Pencil.app" ]; then
    rm -rf "/Applications/Pencil.app" 2>/dev/null || true
elif [ -f "/Applications/Pencil.app" ]; then
    rm -f "/Applications/Pencil.app" 2>/dev/null || true
fi

# Remove $HOME/.pencil
echo "Removing $HOME/.pencil..."
if [ -d "$HOME/.pencil" ]; then
    rm -rf "$HOME/.pencil" 2>/dev/null || true
elif [ -f "$HOME/.pencil" ]; then
    rm -f "$HOME/.pencil" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Pencil
echo "Removing $HOME/Library/Application Support/Pencil..."
if [ -d "$HOME/Library/Application Support/Pencil" ]; then
    rm -rf "$HOME/Library/Application Support/Pencil" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Pencil" ]; then
    rm -f "$HOME/Library/Application Support/Pencil" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/vn.evolus.pencil.plist
echo "Removing $HOME/Library/Preferences/vn.evolus.pencil.plist..."
if [ -d "$HOME/Library/Preferences/vn.evolus.pencil.plist" ]; then
    rm -rf "$HOME/Library/Preferences/vn.evolus.pencil.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/vn.evolus.pencil.plist" ]; then
    rm -f "$HOME/Library/Preferences/vn.evolus.pencil.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
