#!/bin/bash
# Uninstall script for PiBar
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling PiBar..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping PiBar if running..."
pkill -f "PiBar" 2>/dev/null || true

# Remove /Applications/PiBar.app
echo "Removing /Applications/PiBar.app..."
if [ -d "/Applications/PiBar.app" ]; then
    rm -rf "/Applications/PiBar.app" 2>/dev/null || true
elif [ -f "/Applications/PiBar.app" ]; then
    rm -f "/Applications/PiBar.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/net.amiantos.PiBar
echo "Removing $HOME/Library/Application Scripts/net.amiantos.PiBar..."
if [ -d "$HOME/Library/Application Scripts/net.amiantos.PiBar" ]; then
    rm -rf "$HOME/Library/Application Scripts/net.amiantos.PiBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/net.amiantos.PiBar" ]; then
    rm -f "$HOME/Library/Application Scripts/net.amiantos.PiBar" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/net.amiantos.PiBar
echo "Removing $HOME/Library/Containers/net.amiantos.PiBar..."
if [ -d "$HOME/Library/Containers/net.amiantos.PiBar" ]; then
    rm -rf "$HOME/Library/Containers/net.amiantos.PiBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/net.amiantos.PiBar" ]; then
    rm -f "$HOME/Library/Containers/net.amiantos.PiBar" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
