#!/bin/bash
# Uninstall script for PingPlotter
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling PingPlotter..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping PingPlotter if running..."
pkill -f "PingPlotter" 2>/dev/null || true

# Kill application with bundle ID com.pingman.pingplotter.mac if running
echo "Stopping application with bundle ID com.pingman.pingplotter.mac if running..."
killall -9 "com.pingman.pingplotter.mac" 2>/dev/null || true

# Remove /Applications/PingPlotter.app
echo "Removing /Applications/PingPlotter.app..."
if [ -d "/Applications/PingPlotter.app" ]; then
    rm -rf "/Applications/PingPlotter.app" 2>/dev/null || true
elif [ -f "/Applications/PingPlotter.app" ]; then
    rm -f "/Applications/PingPlotter.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/PingPlotter
echo "Removing $HOME/Library/Application Support/PingPlotter..."
if [ -d "$HOME/Library/Application Support/PingPlotter" ]; then
    rm -rf "$HOME/Library/Application Support/PingPlotter" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/PingPlotter" ]; then
    rm -f "$HOME/Library/Application Support/PingPlotter" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/PingPlotter
echo "Removing $HOME/Library/Logs/PingPlotter..."
if [ -d "$HOME/Library/Logs/PingPlotter" ]; then
    rm -rf "$HOME/Library/Logs/PingPlotter" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/PingPlotter" ]; then
    rm -f "$HOME/Library/Logs/PingPlotter" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.pingman.pingplotter.mac.plist
echo "Removing $HOME/Library/Preferences/com.pingman.pingplotter.mac.plist..."
if [ -d "$HOME/Library/Preferences/com.pingman.pingplotter.mac.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.pingman.pingplotter.mac.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.pingman.pingplotter.mac.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.pingman.pingplotter.mac.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.pingman.pingplotter.mac.savedState
echo "Removing $HOME/Library/Saved Application State/com.pingman.pingplotter.mac.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.pingman.pingplotter.mac.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.pingman.pingplotter.mac.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.pingman.pingplotter.mac.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.pingman.pingplotter.mac.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
