#!/bin/bash
# Uninstall script for Plex HTPC
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Plex HTPC..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Plex HTPC if running..."
pkill -f "Plex HTPC" 2>/dev/null || true

# Remove /Applications/Plex HTPC.app
echo "Removing /Applications/Plex HTPC.app..."
if [ -d "/Applications/Plex HTPC.app" ]; then
    rm -rf "/Applications/Plex HTPC.app" 2>/dev/null || true
elif [ -f "/Applications/Plex HTPC.app" ]; then
    rm -f "/Applications/Plex HTPC.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Plex HTPC
echo "Removing $HOME/Library/Application Support/Plex HTPC..."
if [ -d "$HOME/Library/Application Support/Plex HTPC" ]; then
    rm -rf "$HOME/Library/Application Support/Plex HTPC" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Plex HTPC" ]; then
    rm -f "$HOME/Library/Application Support/Plex HTPC" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Plex HTPC
echo "Removing $HOME/Library/Caches/Plex HTPC..."
if [ -d "$HOME/Library/Caches/Plex HTPC" ]; then
    rm -rf "$HOME/Library/Caches/Plex HTPC" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Plex HTPC" ]; then
    rm -f "$HOME/Library/Caches/Plex HTPC" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Plex HTPC
echo "Removing $HOME/Library/Logs/Plex HTPC..."
if [ -d "$HOME/Library/Logs/Plex HTPC" ]; then
    rm -rf "$HOME/Library/Logs/Plex HTPC" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Plex HTPC" ]; then
    rm -f "$HOME/Library/Logs/Plex HTPC" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
