#!/bin/bash
# Uninstall script for Plexamp
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Plexamp..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Plexamp if running..."
pkill -f "Plexamp" 2>/dev/null || true

# Remove /Applications/Plexamp.app
echo "Removing /Applications/Plexamp.app..."
if [ -d "/Applications/Plexamp.app" ]; then
    rm -rf "/Applications/Plexamp.app" 2>/dev/null || true
elif [ -f "/Applications/Plexamp.app" ]; then
    rm -f "/Applications/Plexamp.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Caches/plexamp-updater
echo "Removing $HOME/Library/Application Support/Caches/plexamp-updater..."
if [ -d "$HOME/Library/Application Support/Caches/plexamp-updater" ]; then
    rm -rf "$HOME/Library/Application Support/Caches/plexamp-updater" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Caches/plexamp-updater" ]; then
    rm -f "$HOME/Library/Application Support/Caches/plexamp-updater" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Plexamp
echo "Removing $HOME/Library/Application Support/Plexamp..."
if [ -d "$HOME/Library/Application Support/Plexamp" ]; then
    rm -rf "$HOME/Library/Application Support/Plexamp" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Plexamp" ]; then
