# Remove $HOME/Library/Caches/tv.plex.plexamp*
echo "Removing $HOME/Library/Caches/tv.plex.plexamp*..."
if [ -d "$HOME/Library/Caches/tv.plex.plexamp*" ]; then
    rm -rf "$HOME/Library/Caches/tv.plex.plexamp*" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/tv.plex.plexamp*" ]; then
    rm -f "$HOME/Library/Caches/tv.plex.plexamp*" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Plexamp
echo "Removing $HOME/Library/Logs/Plexamp..."
if [ -d "$HOME/Library/Logs/Plexamp" ]; then
