fi

# Remove $HOME/Library/Preferences/tv.plex.plexamp.plist
echo "Removing $HOME/Library/Preferences/tv.plex.plexamp.plist..."
if [ -d "$HOME/Library/Preferences/tv.plex.plexamp.plist" ]; then
    rm -rf "$HOME/Library/Preferences/tv.plex.plexamp.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/tv.plex.plexamp.plist" ]; then
    rm -f "$HOME/Library/Preferences/tv.plex.plexamp.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/tv.plex.plexamp.savedState
echo "Removing $HOME/Library/Saved Application State/tv.plex.plexamp.savedState..."
if [ -d "$HOME/Library/Saved Application State/tv.plex.plexamp.savedState" ]; then
