#!/bin/bash
# Uninstall script for Polymail
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Polymail..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Polymail if running..."
pkill -f "Polymail" 2>/dev/null || true

# Remove /Applications/Polymail.app
echo "Removing /Applications/Polymail.app..."
if [ -d "/Applications/Polymail.app" ]; then
    rm -rf "/Applications/Polymail.app" 2>/dev/null || true
elif [ -f "/Applications/Polymail.app" ]; then
    rm -f "/Applications/Polymail.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Polymail
echo "Removing $HOME/Library/Application Support/Polymail..."
if [ -d "$HOME/Library/Application Support/Polymail" ]; then
    rm -rf "$HOME/Library/Application Support/Polymail" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Polymail" ]; then
    rm -f "$HOME/Library/Application Support/Polymail" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/io.polymail.osx*
echo "Removing $HOME/Library/Caches/io.polymail.osx*..."
if [ -d "$HOME/Library/Caches/io.polymail.osx*" ]; then
    rm -rf "$HOME/Library/Caches/io.polymail.osx*" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/io.polymail.osx*" ]; then
    rm -f "$HOME/Library/Caches/io.polymail.osx*" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/io.polymail.osx
echo "Removing $HOME/Library/HTTPStorages/io.polymail.osx..."
