elif [ -f "$HOME/Library/HTTPStorages/io.polymail.osx" ]; then
    rm -f "$HOME/Library/HTTPStorages/io.polymail.osx" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Polymail
echo "Removing $HOME/Library/Logs/Polymail..."
if [ -d "$HOME/Library/Logs/Polymail" ]; then
    rm -rf "$HOME/Library/Logs/Polymail" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Polymail" ]; then
    rm -f "$HOME/Library/Logs/Polymail" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/io.polymail.osx.plist
echo "Removing $HOME/Library/Preferences/io.polymail.osx.plist..."
if [ -d "$HOME/Library/Preferences/io.polymail.osx.plist" ]; then
    rm -rf "$HOME/Library/Preferences/io.polymail.osx.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/io.polymail.osx.plist" ]; then
    rm -f "$HOME/Library/Preferences/io.polymail.osx.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/io.polymail.osx.savedState
echo "Removing $HOME/Library/Saved Application State/io.polymail.osx.savedState..."
if [ -d "$HOME/Library/Saved Application State/io.polymail.osx.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/io.polymail.osx.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/io.polymail.osx.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/io.polymail.osx.savedState" 2>/dev/null || true
fi

