#!/bin/bash
# Uninstall script for Positron
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Positron..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Positron if running..."
pkill -f "Positron" 2>/dev/null || true

# Remove /Applications/Positron.app
echo "Removing /Applications/Positron.app..."
if [ -d "/Applications/Positron.app" ]; then
    rm -rf "/Applications/Positron.app" 2>/dev/null || true
elif [ -f "/Applications/Positron.app" ]; then
    rm -f "/Applications/Positron.app" 2>/dev/null || true
fi

# Remove $HOME/.positron
echo "Removing $HOME/.positron..."
if [ -d "$HOME/.positron" ]; then
    rm -rf "$HOME/.positron" 2>/dev/null || true
elif [ -f "$HOME/.positron" ]; then
    rm -f "$HOME/.positron" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Positron
echo "Removing $HOME/Library/Application Support/Positron..."
if [ -d "$HOME/Library/Application Support/Positron" ]; then
    rm -rf "$HOME/Library/Application Support/Positron" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Positron" ]; then
    rm -f "$HOME/Library/Application Support/Positron" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.rstudio.positron.plist
echo "Removing $HOME/Library/Preferences/com.rstudio.positron.plist..."
if [ -d "$HOME/Library/Preferences/com.rstudio.positron.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.rstudio.positron.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.rstudio.positron.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.rstudio.positron.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.rstudio.positron.savedState
echo "Removing $HOME/Library/Saved Application State/com.rstudio.positron.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.rstudio.positron.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.rstudio.positron.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.rstudio.positron.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.rstudio.positron.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
