#!/bin/bash
# Uninstall script for PPPC Utility
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling PPPC Utility..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping PPPC Utility if running..."
pkill -f "PPPC Utility" 2>/dev/null || true

# Kill application with bundle ID com.jamf.opensource.pppcutility if running
echo "Stopping application with bundle ID com.jamf.opensource.pppcutility if running..."
killall -9 "com.jamf.opensource.pppcutility" 2>/dev/null || true

# Remove /Applications/PPPC Utility.app
echo "Removing /Applications/PPPC Utility.app..."
if [ -d "/Applications/PPPC Utility.app" ]; then
    rm -rf "/Applications/PPPC Utility.app" 2>/dev/null || true
elif [ -f "/Applications/PPPC Utility.app" ]; then
    rm -f "/Applications/PPPC Utility.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.jamf.opensource.pppcutility*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.jamf.opensource.pppcutility*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.jamf.opensource.pppcutility*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.jamf.opensource.pppcutility*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.jamf.opensource.pppcutility*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.jamf.opensource.pppcutility*" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.jamf.opensource.pppcutility
echo "Removing $HOME/Library/Containers/com.jamf.opensource.pppcutility..."
if [ -d "$HOME/Library/Containers/com.jamf.opensource.pppcutility" ]; then
    rm -rf "$HOME/Library/Containers/com.jamf.opensource.pppcutility" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.jamf.opensource.pppcutility" ]; then
    rm -f "$HOME/Library/Containers/com.jamf.opensource.pppcutility" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
