# Remove /Applications/PreForm.app
echo "Removing /Applications/PreForm.app..."
if [ -d "/Applications/PreForm.app" ]; then
    rm -rf "/Applications/PreForm.app" 2>/dev/null || true
elif [ -f "/Applications/PreForm.app" ]; then
    rm -f "/Applications/PreForm.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.formlabs.PreForm.plist
echo "Removing $HOME/Library/Preferences/com.formlabs.PreForm.plist..."
