#!/bin/bash
# Uninstall script for Pritunl
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Pritunl..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Pritunl if running..."
pkill -f "Pritunl" 2>/dev/null || true

# Unload service com.pritunl.client
echo "Unloading service com.pritunl.client..."
launchctl unload -w /Library/LaunchAgents/com.pritunl.client.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.pritunl.client.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.pritunl.client.plist 2>/dev/null || true

# Unload service com.pritunl.service
echo "Unloading service com.pritunl.service..."
launchctl unload -w /Library/LaunchAgents/com.pritunl.service.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.pritunl.service.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.pritunl.service.plist 2>/dev/null || true

# Remove $HOME/Library/Application Support/pritunl
echo "Removing $HOME/Library/Application Support/pritunl..."
if [ -d "$HOME/Library/Application Support/pritunl" ]; then
    rm -rf "$HOME/Library/Application Support/pritunl" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/pritunl" ]; then
    rm -f "$HOME/Library/Application Support/pritunl" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/pritunl
echo "Removing $HOME/Library/Caches/pritunl..."
if [ -d "$HOME/Library/Caches/pritunl" ]; then
    rm -rf "$HOME/Library/Caches/pritunl" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/pritunl" ]; then
