fi

# Remove $HOME/Library/Preferences/com.electron.pritunl*
echo "Removing $HOME/Library/Preferences/com.electron.pritunl*..."
if [ -d "$HOME/Library/Preferences/com.electron.pritunl*" ]; then
    rm -rf "$HOME/Library/Preferences/com.electron.pritunl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.electron.pritunl*" ]; then
    rm -f "$HOME/Library/Preferences/com.electron.pritunl*" 2>/dev/null || true
fi

