#!/bin/bash
# Uninstall script for Processing
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Processing..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Processing if running..."
pkill -f "Processing" 2>/dev/null || true

# Kill application with bundle ID org.processing.app if running
echo "Stopping application with bundle ID org.processing.app if running..."
killall -9 "org.processing.app" 2>/dev/null || true

# Remove /Applications/Processing.app
echo "Removing /Applications/Processing.app..."
if [ -d "/Applications/Processing.app" ]; then
    rm -rf "/Applications/Processing.app" 2>/dev/null || true
elif [ -f "/Applications/Processing.app" ]; then
    rm -f "/Applications/Processing.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.processing.app.plist
echo "Removing $HOME/Library/Preferences/org.processing.app.plist..."
if [ -d "$HOME/Library/Preferences/org.processing.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.processing.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.processing.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.processing.app.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.processing.four.plist
echo "Removing $HOME/Library/Preferences/org.processing.four.plist..."
if [ -d "$HOME/Library/Preferences/org.processing.four.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.processing.four.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.processing.four.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.processing.four.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/processing.app.tools.plist
echo "Removing $HOME/Library/Preferences/processing.app.tools.plist..."
if [ -d "$HOME/Library/Preferences/processing.app.tools.plist" ]; then
    rm -rf "$HOME/Library/Preferences/processing.app.tools.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/processing.app.tools.plist" ]; then
    rm -f "$HOME/Library/Preferences/processing.app.tools.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Processing
echo "Removing $HOME/Library/Processing..."
if [ -d "$HOME/Library/Processing" ]; then
    rm -rf "$HOME/Library/Processing" 2>/dev/null || true
elif [ -f "$HOME/Library/Processing" ]; then
    rm -f "$HOME/Library/Processing" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
