#!/bin/bash
# Uninstall script for Proton Mail Bridge
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Proton Mail Bridge..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Proton Mail Bridge if running..."
pkill -f "Proton Mail Bridge" 2>/dev/null || true

# Unload service Proton Mail Bridge
echo "Unloading service Proton Mail Bridge..."
launchctl unload -w /Library/LaunchAgents/Proton Mail Bridge.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/Proton Mail Bridge.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/Proton Mail Bridge.plist 2>/dev/null || true

# Kill application with bundle ID com.protonmail.bridge if running
echo "Stopping application with bundle ID com.protonmail.bridge if running..."
killall -9 "com.protonmail.bridge" 2>/dev/null || true

# Remove /Applications/Proton Mail Bridge.app
echo "Removing /Applications/Proton Mail Bridge.app..."
if [ -d "/Applications/Proton Mail Bridge.app" ]; then
    rm -rf "/Applications/Proton Mail Bridge.app" 2>/dev/null || true
elif [ -f "/Applications/Proton Mail Bridge.app" ]; then
    rm -f "/Applications/Proton Mail Bridge.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/protonmail
echo "Removing $HOME/Library/Application Support/protonmail..."
if [ -d "$HOME/Library/Application Support/protonmail" ]; then
    rm -rf "$HOME/Library/Application Support/protonmail" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/protonmail" ]; then
    rm -f "$HOME/Library/Application Support/protonmail" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Proton AG/Proton Mail Bridge
echo "Removing $HOME/Library/Caches/Proton AG/Proton Mail Bridge..."
if [ -d "$HOME/Library/Caches/Proton AG/Proton Mail Bridge" ]; then
    rm -rf "$HOME/Library/Caches/Proton AG/Proton Mail Bridge" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Proton AG/Proton Mail Bridge" ]; then
    rm -f "$HOME/Library/Caches/Proton AG/Proton Mail Bridge" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/protonmail
echo "Removing $HOME/Library/Caches/protonmail..."
if [ -d "$HOME/Library/Caches/protonmail" ]; then
    rm -rf "$HOME/Library/Caches/protonmail" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/protonmail" ]; then
    rm -f "$HOME/Library/Caches/protonmail" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
