#!/bin/bash
# Uninstall script for QGIS
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling QGIS..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping QGIS if running..."
pkill -f "QGIS" 2>/dev/null || true

# Remove /Applications/QGIS.app
echo "Removing /Applications/QGIS.app..."
if [ -d "/Applications/QGIS.app" ]; then
    rm -rf "/Applications/QGIS.app" 2>/dev/null || true
elif [ -f "/Applications/QGIS.app" ]; then
    rm -f "/Applications/QGIS.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/QGIS
echo "Removing $HOME/Library/Application Support/QGIS..."
if [ -d "$HOME/Library/Application Support/QGIS" ]; then
    rm -rf "$HOME/Library/Application Support/QGIS" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/QGIS" ]; then
    rm -f "$HOME/Library/Application Support/QGIS" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/QGIS
echo "Removing $HOME/Library/Caches/QGIS..."
if [ -d "$HOME/Library/Caches/QGIS" ]; then
    rm -rf "$HOME/Library/Caches/QGIS" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/QGIS" ]; then
    rm -f "$HOME/Library/Caches/QGIS" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.qgis.qgis*.savedState
echo "Removing $HOME/Library/Saved Application State/org.qgis.qgis*.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.qgis.qgis*.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.qgis.qgis*.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.qgis.qgis*.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.qgis.qgis*.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
