#!/bin/bash
# Uninstall script for Quicksilver
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Quicksilver..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Quicksilver if running..."
pkill -f "Quicksilver" 2>/dev/null || true

# Remove /Applications/Quicksilver.app
echo "Removing /Applications/Quicksilver.app..."
if [ -d "/Applications/Quicksilver.app" ]; then
    rm -rf "/Applications/Quicksilver.app" 2>/dev/null || true
elif [ -f "/Applications/Quicksilver.app" ]; then
    rm -f "/Applications/Quicksilver.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Quicksilver
echo "Removing $HOME/Library/Application Support/Quicksilver..."
if [ -d "$HOME/Library/Application Support/Quicksilver" ]; then
    rm -rf "$HOME/Library/Application Support/Quicksilver" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Quicksilver" ]; then
    rm -f "$HOME/Library/Application Support/Quicksilver" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.blacktree.Quicksilver.plist
echo "Removing $HOME/Library/Preferences/com.blacktree.Quicksilver.plist..."
if [ -d "$HOME/Library/Preferences/com.blacktree.Quicksilver.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.blacktree.Quicksilver.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.blacktree.Quicksilver.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.blacktree.Quicksilver.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
