#!/bin/bash
# Uninstall script for Radio Silence
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Radio Silence..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Radio Silence if running..."
pkill -f "Radio Silence" 2>/dev/null || true

# Unload service com.radiosilenceapp.agent
echo "Unloading service com.radiosilenceapp.agent..."
launchctl unload -w /Library/LaunchAgents/com.radiosilenceapp.agent.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.radiosilenceapp.agent.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.radiosilenceapp.agent.plist 2>/dev/null || true

# Unload service com.radiosilenceapp.nke
echo "Unloading service com.radiosilenceapp.nke..."
launchctl unload -w /Library/LaunchAgents/com.radiosilenceapp.nke.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.radiosilenceapp.nke.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.radiosilenceapp.nke.plist 2>/dev/null || true

# Unload service com.radiosilenceapp.trial
echo "Unloading service com.radiosilenceapp.trial..."
launchctl unload -w /Library/LaunchAgents/com.radiosilenceapp.trial.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.radiosilenceapp.trial.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.radiosilenceapp.trial.plist 2>/dev/null || true

# Kill application with bundle ID com.radiosilenceapp.client if running
echo "Stopping application with bundle ID com.radiosilenceapp.client if running..."
killall -9 "com.radiosilenceapp.client" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Radio Silence
echo "Removing $HOME/Library/Application Support/Radio Silence..."
if [ -d "$HOME/Library/Application Support/Radio Silence" ]; then
    rm -rf "$HOME/Library/Application Support/Radio Silence" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Radio Silence" ]; then
    rm -f "$HOME/Library/Application Support/Radio Silence" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
