#!/bin/bash
# Uninstall script for RapidWeaver
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling RapidWeaver..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping RapidWeaver if running..."
pkill -f "RapidWeaver" 2>/dev/null || true

# Remove /Applications/RapidWeaver.app
echo "Removing /Applications/RapidWeaver.app..."
if [ -d "/Applications/RapidWeaver.app" ]; then
    rm -rf "/Applications/RapidWeaver.app" 2>/dev/null || true
elif [ -f "/Applications/RapidWeaver.app" ]; then
    rm -f "/Applications/RapidWeaver.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/*.com.realmacsoftware.rapidweaver*
echo "Removing $HOME/Library/Application Scripts/*.com.realmacsoftware.rapidweaver*..."
if [ -d "$HOME/Library/Application Scripts/*.com.realmacsoftware.rapidweaver*" ]; then
    rm -rf "$HOME/Library/Application Scripts/*.com.realmacsoftware.rapidweaver*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/*.com.realmacsoftware.rapidweaver*" ]; then
    rm -f "$HOME/Library/Application Scripts/*.com.realmacsoftware.rapidweaver*" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.realmacsoftware.rapidweaver*
echo "Removing $HOME/Library/Containers/com.realmacsoftware.rapidweaver*..."
if [ -d "$HOME/Library/Containers/com.realmacsoftware.rapidweaver*" ]; then
    rm -rf "$HOME/Library/Containers/com.realmacsoftware.rapidweaver*" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.realmacsoftware.rapidweaver*" ]; then
    rm -f "$HOME/Library/Containers/com.realmacsoftware.rapidweaver*" 2>/dev/null || true
fi

# Remove $HOME/Library/Group Containers/*.com.realmacsoftware.rapidweaver*
echo "Removing $HOME/Library/Group Containers/*.com.realmacsoftware.rapidweaver*..."
if [ -d "$HOME/Library/Group Containers/*.com.realmacsoftware.rapidweaver*" ]; then
    rm -rf "$HOME/Library/Group Containers/*.com.realmacsoftware.rapidweaver*" 2>/dev/null || true
elif [ -f "$HOME/Library/Group Containers/*.com.realmacsoftware.rapidweaver*" ]; then
    rm -f "$HOME/Library/Group Containers/*.com.realmacsoftware.rapidweaver*" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
